    {
        "key": "ctrl+enter",
        "command": "-notebook.cell.insertCodeCellBelow",
        "when": "notebookCellListFocused && !inputFocus"
    },
    {
        "key": "ctrl+enter",
        "command": "-catalyst.runAllTestCases"
    },
    {
        "key": "shift+enter e",
        "command": "extension.jumpy-word"
    },
    {
        "key": "shift+enter l",
        "command": "extension.jumpy-line"
    }
